<script>
    // Page Route Local Storage
    localStorage.setItem('storedRoute', '/userTemplates');

    // Logged in user
    import { user } from "../user.js";
    let loggedInUser;
    user.subscribe(value => (
        loggedInUser = value           
    ));
    
    // Sidebar
    import Sidebar from "../lib/Sidebar.svelte";

    // Lib
    import SearchProfileBar from "../lib/SearchProfileBar.svelte";

    // Template sidebar
    let selectedButton = null;
    let selectedTemplate = null;
    const toggle = (button) => {
        // Toggle the selected state
        selectedButton = selectedButton === button ? null : button;

        if(button === 1) {
            selectedTemplate = 'design';
        } else if(button === 2) {
            selectedTemplate = 'background';
        } else if(button === 3) {
            selectedTemplate = 'text';
        } else if(button === 4) {
            selectedTemplate = 'video';
        } else if(button === 5) {
            selectedTemplate = 'image';
        } else if(button === 6) {
            selectedTemplate = 'poll';
        } else if(button === 7) {
            selectedTemplate = 'files';
        } 
    };
</script>

